<script>
import commonHeader from '../../components/commonHeader.vue';
export default {
    components: { commonHeader },
    data() {
        return {
            eleArr: [],
            eleActiveArr: Array(6)
        }   
    },
    mounted() {
        for(let i=1; i<=6; i++) {
            let ele = document.getElementById('wrap' + i);
            this.eleArr.push(ele);
            this.$set(this.eleActiveArr, i-1, false);
        }
        this.$nextTick(() => {
            window.addEventListener('scroll', this.pageActiveListener);
        })
    },

    methods: {
        pageActiveListener() {
            for(let i=0; i<6; i++) {
                if(this.checkInpage(this.eleArr[i])) {
                    this.setElementActive(i);
                }
            }

            if(!this.eleActiveArr.includes(false)) {
                window.removeEventListener('scroll', this.pageActiveListener)
            }
        },

        checkInpage(el) {
            const pageHeight = document.documentElement.clientHeight

            const contentTop = el.getBoundingClientRect().top
            const contentHeight = el.offsetHeight

            return (contentTop < pageHeight && contentTop>=0)
                || (contentTop<0 && (contentTop+contentHeight>0));
        },

        setElementActive(i) {
            this.$set(this.eleActiveArr, i, true);
        }
    }
}
</script>

